import React from "react";
import { Segment, Card, Grid } from "semantic-ui-react";
import ArticleCard from "./ArticleCard";

const FeaturedArticles = () => {
  // Manually insert featured articles
  const featuredArticles = [
    {
      id: 1,
      image:
        "data:image/jpeg;base64,",
      title: "Ramdom in death",
      description:
        "A deep dive into React Hooks and how to use them effectively.",
      author: "J D Robb",
      rating: 4,
    },
    {
      id: 2,
      image:
        "data:image/jpeg;base64,",
      title: "The book of Random Oddities",
      description:
        "Learn how to create a RESTful API using Node.js and Express.",
      author: "Jane Smith",
      rating: 5,
    },
    {
      id: 3,
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTkvf-Pl7UVuDz3lBbv6Eqa_Ng26Gmj39mx-A&usqp=CAU",
      title: "Random acts of kindness",
      description: "A comprehensive guide to CSS Flexbox layout.",
      author: "Sam Wilson",
      rating: 4,
    },
  ];

  return (
    <Segment>
      <div className="featured-articles-header" style={{margin:"20px"}}>
        <h2>GENERAL INFORMATION</h2>
      </div>
      <Card.Group centered>
          <div className="Arts">

          {featuredArticles.map((article) => (
              <ArticleCard
                image={article.image}
                title={article.title}
                description={article.description}
                author={article.author}
                rating={article.rating}
              />
          ))}
          </div>
      </Card.Group>
    </Segment>
  );
};

export default FeaturedArticles;
